mapper.update:
update company
set company_name = #{companyName,jdbcType=VARCHAR},
  company_location = #{companyLocation,jdbcType=VARCHAR},
  company_contact = #{companyContact,jdbcType=VARCHAR},
  company_birthday = #{companyBirthday,jdbcType=DATE}
where company_id = #{companyId,jdbcType=INTEGER}

mapper.select:
select company_id, company_name, company_location, company_contact, company_birthday
from company
where company_id = #{companyId,jdbcType=INTEGER}

select company_id, company_name, company_location, company_contact, company_birthday
from company
where company_name = #{companyName,jdbcType=VARCHAR}
