select store_id, store_name, store_type, store_own, marketplace_id, nws_auth_token
from store
where store_own = #{bvoId,jdbcType=INTEGER}  and store_type = #{storeType,jdbcType=INTEGER}

select store_id, store_name, store_type, store_own, marketplace_id, nws_auth_token
from store
where store_own = #{storeOwn,jdbcType=INTEGER}
