mapper.update:
update brand
set brand_name = #{brandName,jdbcType=VARCHAR},
  brand_com_id = #{brandComId,jdbcType=INTEGER},
  brand_mer_id = #{brandMerId,jdbcType=INTEGER}
where brand_id = #{brandId,jdbcType=INTEGER}

mapper.select:
select brand_id, brand_name, brand_com_id, brand_mer_id
from brand
where brand_id = #{brandId,jdbcType=INTEGER}
