mapper.update:
update goods_size
set lenght = #{lenght,jdbcType=REAL},
  width = #{width,jdbcType=REAL},
  height = #{height,jdbcType=REAL},
  weight = #{weight,jdbcType=REAL}
where goods_id = #{goodsId,jdbcType=INTEGER}

mapper.select:
select goods_id, lenght, width, height, weight
from goods_size
where goods_id = #{goodsId,jdbcType=INTEGER}
