mapper.insert:
insert into parameter (par_id, par_value, par_describe
  )
values (#{parId,jdbcType=VARCHAR}, #{parValue,jdbcType=INTEGER}, #{parDescribe,jdbcType=VARCHAR}
  )

mapper.update:
